function run(n="*",e=(n=>{})){n=n.split(" "),uAgn=window.navigator.userAgent,n.forEach(n=>{switch(n){case"*":e(uAgn);break;case"windows":/Windows/.test(uAgn)&&e(uAgn);break;case"linux":/Linux/.test(uAgn)&&e(uAgn);break;case"mac-os-x":/Mac OS X/.test(uAgn)&&e(uAgn);break;case"ios":/(iPhone|iPad|iPod)/.test(uAgn)&&e(uAgn);break;case"android":/Android/.test(uAgn)&&e(uAgn)}})}